// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;

/**
 * @title Sapphire
 * @notice This library provides a number of convenient wrappers for
 * cryptographic operations such as the x25519 key derivation, Deoxys-II-based
 * encryption and decryption, signing key generation, message digest signing and
 * verification, gas padding and hashing.
 *
 * Most of the mentioned functions are implemented as Sapphire's precompiles and
 * are cheap to call.
 *
 * #### Calling Precompiles Manually
 *
 * You can override the wrappers and call Sapphire precompiles by dispatching
 * calls to specific well-known contract addresses, as described below. The
 * __Precompile address__ section of each function will show you the address
 * of the corresponding precompile.
 *
 * Input parameters should be packed into a contiguous memory region with each
 * chunk of data padded to 32 bytes as usual. The recommended way to construct
 * parameter byte sequences in Solidity is with `abi.encode` and `abi.decode`,
 * which will transparently handle things like putting `bytes` lengths in the
 * correct position.
 */
library Sapphire {
    // Oasis-specific, confidential precompiles
    address internal constant RANDOM_BYTES =
        0x0100000000000000000000000000000000000001;
    address internal constant DERIVE_KEY =
        0x0100000000000000000000000000000000000002;
    address internal constant ENCRYPT =
        0x0100000000000000000000000000000000000003;
    address internal constant DECRYPT =
        0x0100000000000000000000000000000000000004;
    address internal constant GENERATE_SIGNING_KEYPAIR =
        0x0100000000000000000000000000000000000005;
    address internal constant SIGN_DIGEST =
        0x0100000000000000000000000000000000000006;
    address internal constant VERIFY_DIGEST =
        0x0100000000000000000000000000000000000007;
    address internal constant CURVE25519_PUBLIC_KEY =
        0x0100000000000000000000000000000000000008;
    address internal constant GAS_USED =
        0x0100000000000000000000000000000000000009;
    address internal constant PAD_GAS =
        0x010000000000000000000000000000000000000a;

    // Oasis-specific, general precompiles
    address internal constant SHA512_256 =
        0x0100000000000000000000000000000000000101;
    address internal constant SHA512 =
        0x0100000000000000000000000000000000000102;
    address internal constant SHA384 =
        0x0100000000000000000000000000000000000104;

    type Curve25519PublicKey is bytes32;
    type Curve25519SecretKey is bytes32;

    enum SigningAlg {
        /// Ed25519 signature over the provided message using SHA-512/265 with a domain separator.
        /// Can be used to sign transactions for the Oasis consensus layer and SDK paratimes.
        Ed25519Oasis,
        /// Ed25519 signature over the provided message.
        Ed25519Pure,
        /// Ed25519 signature over the provided prehashed SHA-512 digest.
        Ed25519PrehashedSha512,
        /// Secp256k1 signature over the provided message using SHA-512/256 with a domain separator.
        /// Can be used to sign transactions for the Oasis consensus layer and SDK paratimes.
        Secp256k1Oasis,
        /// Secp256k1 over the provided Keccak256 digest.
        /// Can be used to sign transactions for Ethereum-compatible networks.
        Secp256k1PrehashedKeccak256,
        /// Secp256k1 signature over the provided SHA-256 digest.
        Secp256k1PrehashedSha256,
        /// Sr25519 signature over the provided message.
        Sr25519,
        /// Secp256r1 signature over the provided SHA-256 digest.
        Secp256r1PrehashedSha256,
        /// Secp384r1 signature over the provided SHA-384 digest.
        Secp384r1PrehashedSha384
    }

    /**
     * @notice Generate `num_bytes` pseudo-random bytes, with an optional
     * personalization string (`pers`) added into the hashing algorithm to
     * increase domain separation when needed.
     *
     * #### Precompile address
     *
     * `0x0100000000000000000000000000000000000001`
     *
     * #### Gas cost
     *
     * 10,000 minimum plus 240 per output word plus 60 per word of the
     * personalization string.
     *
     * #### Implementation details
     *
     * The mode (e.g. simulation or "view call" vs transaction execution) is fed
     * to TupleHash (among other block-dependent components) to derive the "key
     * id", which is then used to derive a per-block VRF key from
     * epoch-ephemeral entropy (using KMAC256 and cSHAKE) so a different key
     * id will result in a unique per-block VRF key. This per-block VRF key is
     * then used to create the per-block root RNG which is then used to derive
     * domain-separated (using Merlin transcripts) per-transaction random RNGs
     * which are then exposed via this precompile. The KMAC, cSHAKE and
     * TupleHash algorithms are SHA-3 derived functions defined in [NIST
     * Special Publication 800-185](https://nvlpubs.nist.gov/nistpubs/specialpublications/nist.sp.800-185.pdf).
     *
     * #### Example
     *
     * ```solidity
     * bytes memory randomPad = Sapphire.randomBytes(64, "");
     * ```
     *
     * @param numBytes The number of bytes to return.
     * @param pers An optional personalization string to increase domain
     *        separation.
     * @return The random bytes. If the number of bytes requested is too large
     *         (over 1024), a smaller amount (1024) will be returned.
     */
    function randomBytes(uint256 numBytes, bytes memory pers)
        internal
        view
        returns (bytes memory)
    {
        (bool success, bytes memory entropy) = RANDOM_BYTES.staticcall(
            abi.encode(numBytes, pers)
        );
        require(success, "randomBytes: failed");
        return entropy;
    }

    /**
     * @notice Generates a Curve25519 keypair.
     * @param pers An optional personalization string used to add domain
     * separation.
     * @return pk The Curve25519 public key. Useful for key exchange.
     * @return sk The Curve25519 secret key. Pairs well with
     * [deriveSymmetricKey](#derivesymmetrickey).
     */
    function generateCurve25519KeyPair(bytes memory pers)
        internal
        view
        returns (Curve25519PublicKey pk, Curve25519SecretKey sk)
    {
        bytes memory scalar = randomBytes(32, pers);
        // Twiddle some bits, as per RFC 7748 ยง5.
        scalar[0] &= 0xf8; // Make it a multiple of 8 to avoid small subgroup attacks.
        scalar[31] &= 0x7f; // Clamp to < 2^255 - 19
        scalar[31] |= 0x40; // Clamp to >= 2^254
        (bool success, bytes memory pkBytes) = CURVE25519_PUBLIC_KEY.staticcall(
            scalar
        );
        require(success, "gen curve25519 pk: failed");
        return (
            Curve25519PublicKey.wrap(bytes32(pkBytes)),
            Curve25519SecretKey.wrap(bytes32(scalar))
        );
    }

    /**
     * @notice Derive a symmetric key from a pair of keys using x25519.
     *
     * #### Precompile address
     *
     * `0x0100000000000000000000000000000000000002`
     *
     * #### Gas cost
     *
     * 100,000
     *
     * #### Example
     *
     * ```solidity
     * bytes32 publicKey = ... ;
     * bytes32 privateKey = ... ;
     * bytes32 symmetric = Sapphire.deriveSymmetricKey(publicKey, privateKey);
     * ```
     *
     * @param peerPublicKey The peer's public key.
     * @param secretKey Your secret key.
     * @return A derived symmetric key.
     */
    function deriveSymmetricKey(
        Curve25519PublicKey peerPublicKey,
        Curve25519SecretKey secretKey
    ) internal view returns (bytes32) {
        (bool success, bytes memory symmetric) = DERIVE_KEY.staticcall(
            abi.encode(peerPublicKey, secretKey)
        );
        require(success, "deriveSymmetricKey: failed");
        return bytes32(symmetric);
    }

    /**
     * @notice Encrypt and authenticate the plaintext and additional data using
     * DeoxysII.
     *
     * #### Precompile address
     *
     * `0x0100000000000000000000000000000000000003`
     *
     * #### Gas cost
     *
     * 50,000 minimum plus 100 per word of input
     *
     * #### Example
     *
     * ```solidity
     * bytes32 key = ... ;
     * bytes32 nonce = ... ;
     * bytes memory text = "plain text";
     * bytes memory ad = "additional data";
     * bytes memory encrypted = Sapphire.encrypt(key, nonce, text, ad);
     * bytes memory decrypted = Sapphire.decrypt(key, nonce, encrypted, ad);
     * ```
     *
     * @param key The key to use for encryption.
     * @param nonce The nonce. Note that only the first 15 bytes of this
     * parameter are used.
     * @param plaintext The plaintext to encrypt and authenticate.
     * @param additionalData The additional data to authenticate.
     * @return The ciphertext with appended auth tag.
     */
    function encrypt(
        bytes32 key,
        bytes32 nonce,
        bytes memory plaintext,
        bytes memory additionalData
    ) internal view returns (bytes memory) {
        (bool success, bytes memory ciphertext) = ENCRYPT.staticcall(
            abi.encode(key, nonce, plaintext, additionalData)
        );
        require(success, "encrypt: failed");
        return ciphertext;
    }

    /**
     * @notice Decrypt and authenticate the ciphertext and additional data using
     * DeoxysII. Reverts if the auth tag is incorrect.
     *
     * #### Precompile address
     *
     * `0x0100000000000000000000000000000000000004`
     *
     * #### Gas cost
     *
     * 50,000 minimum plus 100 per word of input
     *
     * #### Example
     *
     * ```solidity
     * bytes32 key = ... ;
     * bytes32 nonce = ... ;
     * bytes memory text = "plain text";
     * bytes memory ad = "additional data";
     * bytes memory encrypted = Sapphire.encrypt(key, nonce, text, ad);
     * bytes memory decrypted = Sapphire.decrypt(key, nonce, encrypted, ad);
     * ```
     *
     * @param key The key to use for decryption.
     * @param nonce The nonce. Note that only the first 15 bytes of this
     * parameter are used.
     * @param ciphertext The ciphertext with tag to decrypt and authenticate.
     * @param additionalData The additional data to authenticate against the
     * ciphertext.
     * @return The original plaintext.
     */
    function decrypt(
        bytes32 key,
        bytes32 nonce,
        bytes memory ciphertext,
        bytes memory additionalData
    ) internal view returns (bytes memory) {
        (bool success, bytes memory plaintext) = DECRYPT.staticcall(
            abi.encode(key, nonce, ciphertext, additionalData)
        );
        require(success, "decrypt: failed");
        return plaintext;
    }

    /**
     * @notice Generate a public/private key pair using the specified method and
     * seed. The available methods are items in the
     * [`Sapphire.SigningAlg`](#signingalg) enum. Note, however, that the
     * generation method ignores subvariants, so all three Ed25519-based are
     * equivalent, and all Secp256k1 & Secp256r1 based methods are equivalent.
     * Sr25519 is not available and will return an error.
     *
     * #### Precompile address
     * `0x0100000000000000000000000000000000000005`
     *
     * #### Gas Cost
     *
     * ##### Ed25519: 1,000 gas
     *
     * - `0` (`Ed25519Oasis`)
     * - `1` (`Ed25519Pure`)
     * - `2` (`Ed25519PrehashedSha512`)
     *
     * ##### Secp256k1: 1,500 gas.
     * - `3` (`Secp256k1Oasis`)
     * - `4` (`Secp256k1PrehashedKeccak256`)
     * - `5` (`Secp256k1PrehashedSha256`)
     *
     * ##### Secp256r1: 4,000 gas
     * - `7` (`Secp256r1PrehashedSha256`)
     *
     * ##### Secp384r1: 18,000 gas
     * - `8` (`Secp384r1PrehashedSha384`)
     *
     * #### Key Formats
     *
     * ##### Ed25519
     *
     * Public key: 32 bytes
     * Secret key: 32 bytes
     *
     * ##### Secp256k1 & Secp256r1
     *
     * Public key: 33 bytes, compressed format (`0x02` or `0x03` prefix, then 32
     * byte X coordinate).
     * Secret key: 32 bytes
     *
     * ##### Secp384r1
     *
     * Public key: 49 bytes, compressed format (`0x02` or `0x03` prefix, then 48
     * byte X coordinate).
     * Secret key: 48 bytes
     *
     * #### Example
     *
     * ```solidity
     * bytes memory seed = hex"0123456789abcdef0123456789abcdef0123456789abcdef0123456789abcdef";
     * bytes memory publicKey;
     * bytes memory privateKey;
     * (publicKey, privateKey) = Sapphire.generateSigningKeyPair(Sapphire.SigningAlg.Ed25519Pure, seed);
     * ```
     *
     * @param alg The signing alg for which to generate a keypair.
     * @param seed The seed to use for generating the key pair. You can use the
     * `randomBytes` method if you don't already have a seed.
     * @return publicKey The public part of the keypair.
     * @return secretKey The secret part of the keypair.
     */
    function generateSigningKeyPair(SigningAlg alg, bytes memory seed)
        internal
        view
        returns (bytes memory publicKey, bytes memory secretKey)
    {
        (bool success, bytes memory keypair) = GENERATE_SIGNING_KEYPAIR
            .staticcall(abi.encode(alg, seed));
        require(success, "gen signing keypair: failed");
        return abi.decode(keypair, (bytes, bytes));
    }

    /**
     * @notice Sign a message within the provided context using the specified
     * algorithm, and return the signature. The `context_or_digest` and
     * `messages` parameters change in meaning slightly depending on the method
     * requested. For methods that take a context in addition to the message you
     * must pass the context in the `context_or_digest` parameter and use
     * `message` as expected. For methods that take a pre-existing hash of the
     * message, pass that in `context_or_digest` and leave `message` empty.
     * Specifically the `Ed25519Oasis` and `Secp256k1Oasis` variants take both a
     * context and a message (each are variable length `bytes`), the context
     * serves as a domain separator.
     *
     * #### Precompile address
     *
     * `0x0100000000000000000000000000000000000006`
     *
     * #### Gas cost
     *
     * See below for the method-dependent base cost, plus 8 gas per 32 bytes of
     * context and message except digest.
     *
     * #### Signing algorithms
     *
     * - `0` (`Ed25519Oasis`): 1,500 gas, variable length context and message.
     * - `1` (`Ed25519Pure`): 1,500 gas, empty context, variable length message.
     * - `2` (`Ed25519PrehashedSha512`): 1,500 gas, pre-existing SHA-512 hash
     *   (64 bytes) as context, empty message.
     * - `3` (`Secp256k1Oasis`): 3,000 gas, variable length context and message
     * - `4` (`Secp256k1PrehashedKeccak256`): 3,000 gas, pre-existing hash
     *   (32 bytes) as context, empty message.
     * - `5` (`Secp256k1PrehashedSha256`): 3,000 gas, pre-existing hash (32
     *   bytes) as context, empty message.
     * - `7` (`Secp256r1PrehashedSha256`): 9,000 gas, pre-existing hash (32
     *   bytes) as context, empty message.
     * - `8` (`Secp384r1PrehashedSha384`): 43,200 gas, pre-existing hash (32
     *   bytes) as context, empty message.
     *
     * #### Example
     *
     * ```solidity
     * Sapphire.SigningAlg alg = Sapphire.SigningAlg.Ed25519Pure;
     * bytes memory pk;
     * bytes memory sk;
     * (pk, sk) = Sapphire.generateSigningKeyPair(alg, Sapphire.randomBytes(32, ""));
     * bytes memory signature = Sapphire.sign(alg, sk, "", "signed message");
     * ```
     *
     * @param alg The signing algorithm to use.
     * @param secretKey The secret key to use for signing. The key must be valid
     * for use with the requested algorithm.
     * @param contextOrHash Domain-Separator Context, or precomputed hash bytes.
     * @param message Message to sign, should be zero-length if precomputed hash
     * given.
     * @return signature The resulting signature.
     * @custom:see @oasisprotocol/oasis-sdk :: precompile/confidential.rs :: call_sign
     */
    function sign(
        SigningAlg alg,
        bytes memory secretKey,
        bytes memory contextOrHash,
        bytes memory message
    ) internal view returns (bytes memory signature) {
        (bool success, bytes memory sig) = SIGN_DIGEST.staticcall(
            abi.encode(alg, secretKey, contextOrHash, message)
        );
        require(success, "sign: failed");
        return sig;
    }

    /**
     * @notice Verifies that the provided digest was signed with using the
     * secret key corresponding to the provided private key and the specified
     * signing algorithm.
     *
     * The `method`, `context_or_digest` and `message` parameters have the same
     * meaning as described above in the [sign()](#sign) function.
     *
     * #### Precompile address
     *
     * `0x0100000000000000000000000000000000000007`
     *
     * #### Gas cost
     *
     * The algorithm-specific base cost below, with an additional **8 gas per
     * 32 bytes** of `context` and `message` for the `Ed25519Oasis`,
     * `Ed25519Pure` and `Secp256k1Oasis` algorithms.
     *
     * - `0` (`Ed25519Oasis`): 2,000 gas
     * - `1` (`Ed25519Pure`): 2,000 gas
     * - `2` (`Ed25519PrehashedSha512`): 2,000 gas
     * - `3` (`Secp256k1Oasis`): 3,000 gas
     * - `4` (`Secp256k1PrehashedKeccak256`): 3,000 gas
     * - `5` (`Secp256k1PrehashedSha256`): 3,000 gas
     * - `7` (`Secp256r1PrehashedSha256`): 7,900 gas
     * - `8` (`Secp384r1PrehashedSha384`): 37,920 gas
     *
     * #### Example
     *
     * ```solidity
     * Sapphire.SigningAlg alg = Sapphire.SigningAlg.Secp256k1PrehashedKeccak256;
     * bytes memory pk;
     * bytes memory sk;
     * bytes memory digest = abi.encodePacked(keccak256("signed message"));
     * (pk, sk) = Sapphire.generateSigningKeyPair(alg, Sapphire.randomBytes(32, ""));
     * bytes memory signature = Sapphire.sign(alg, sk, digest, "");
     * require( Sapphire.verify(alg, pk, digest, "", signature) );
     * ```
     *
     * @param alg The signing algorithm by which the signature was generated.
     * @param publicKey The public key against which to check the signature.
     * @param contextOrHash Domain-Separator Context, or precomputed hash bytes
     * @param message The hash of the message that was signed, should be
     * zero-length if precomputed hash was given.
     * @param signature The signature to check.
     * @return verified Whether the signature is valid for the given parameters.
     * @custom:see @oasisprotocol/oasis-sdk :: precompile/confidential.rs :: call_verify
     */
    function verify(
        SigningAlg alg,
        bytes memory publicKey,
        bytes memory contextOrHash,
        bytes memory message,
        bytes memory signature
    ) internal view returns (bool verified) {
        (bool success, bytes memory v) = VERIFY_DIGEST.staticcall(
            abi.encode(alg, publicKey, contextOrHash, message, signature)
        );
        require(success, "verify: failed");
        return abi.decode(v, (bool));
    }

    /**
     * @notice Set the current transactions gas usage to a specific amount
     * @dev Will cause a reversion if the current usage is more than the amount.
     * @param toAmount Gas usage will be set to this amount
     * @custom:see @oasisprotocol/oasis-sdk :: precompile/gas.rs :: call_pad_gas
     *
     */
    function padGas(uint128 toAmount) internal view {
        (bool success, ) = PAD_GAS.staticcall(abi.encode(toAmount));
        require(success, "verify: failed");
    }

    /**
     * @notice Returns the amount of gas currently used by the transaction
     * @custom:see @oasisprotocol/oasis-sdk :: precompile/gas.rs :: call_gas_used
     */
    function gasUsed() internal view returns (uint64) {
        (bool success, bytes memory v) = GAS_USED.staticcall("");
        require(success, "gasused: failed");
        return abi.decode(v, (uint64));
    }
}

/**
 * @notice Hash the input data with SHA-512/256, according to
 * [NIST.FIPS.180-4](https://nvlpubs.nist.gov/nistpubs/FIPS/NIST.FIPS.180-4.pdf).
 *
 * #### Precompile address
 *
 * `0x0100000000000000000000000000000000000102`
 *
 * #### Gas cost
 *
 * 115 gas, then 13 gas per word
 *
 * #### Example
 *
 * ```solidity
 * bytes32 result = sha512_256(abi.encodePacked("input data"));
 * ```
 *
 * #### Warning: SHA-512 vs SHA-512/256 Length-Extension Attacks
 *
 * [SHA-512](function.sha512.md#sha512) is vulnerable to [length-extension
 * attacks](https://en.wikipedia.org/wiki/Length_extension_attack), which are
 * relevant if you are computing the hash of a secret message. The
 * [SHA-512/256](function.sha512_256.md#sha512_256) variant is **not**
 * vulnerable to length-extension attacks.
 *
 * @param input Bytes to hash.
 * @return result 32 byte digest.
 * @custom:standard https://nvlpubs.nist.gov/nistpubs/FIPS/NIST.FIPS.180-4.pdf
 * @custom:see @oasisprotocol/oasis-sdk :: precompile/sha2.rs :: call_sha512_256
 */
function sha512_256(bytes memory input) view returns (bytes32 result) {
    (bool success, bytes memory output) = Sapphire.SHA512_256.staticcall(input);

    require(success, "sha512_256");

    return bytes32(output);
}

/**
 * @notice Hash the input data with SHA-512, according to
 * [NIST.FIPS.180-4](https://nvlpubs.nist.gov/nistpubs/FIPS/NIST.FIPS.180-4.pdf)
 *
 * #### Precompile address
 *
 * `0x0100000000000000000000000000000000000101`
 *
 * #### Warning: SHA-512 vs SHA-512/256 Length-Extension Attacks
 *
 * [SHA-512](function.sha512.md#sha512) is vulnerable to [length-extension
 * attacks](https://en.wikipedia.org/wiki/Length_extension_attack), which are
 * relevant if you are computing the hash of a secret message. The
 * [SHA-512/256](function.sha512_256.md#sha512_256) variant is **not**
 * vulnerable to length-extension attacks.
 *
 * #### Gas Cost
 *
 * 115 gas, then 13 gas per word
 *
 * #### Example
 *
 * ```solidity
 * bytes memory result = sha512(abi.encodePacked("input data"));
 * ```
 *
 * @param input Bytes to hash.
 * @return output 64 byte digest.
 * @custom:standard https://nvlpubs.nist.gov/nistpubs/FIPS/NIST.FIPS.180-4.pdf
 * @custom:see @oasisprotocol/oasis-sdk :: precompile/sha2.rs :: call_sha512
 */
function sha512(bytes memory input) view returns (bytes memory output) {
    bool success;

    (success, output) = Sapphire.SHA512.staticcall(input);

    require(success, "sha512");
}

/**
 * @notice Hash the input data with SHA-384.
 * @param input Bytes to hash.
 * @return output 48 byte digest.
 * @custom:standard https://nvlpubs.nist.gov/nistpubs/FIPS/NIST.FIPS.180-4.pdf
 * @custom:see @oasisprotocol/oasis-sdk :: precompile/sha2.rs :: call_sha384
 */
function sha384(bytes memory input) view returns (bytes memory output) {
    bool success;

    (success, output) = Sapphire.SHA384.staticcall(input);

    require(success, "sha384");
}

contracts/LifeSignalRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@oasisprotocol/sapphire-contracts/contracts/Sapphire.sol";

/**
 * @title LifeSignalRegistry
 * @dev Manages owner registration, contacts, vaults, and death declarations with privacy features
 */
contract LifeSignalRegistry {

    // Structures
    struct Owner {
        address addr;
        string firstName;
        string lastName;
        string email;
        string phone;
        uint256 lastHeartbeat;
        uint256 graceInterval; // in seconds
        bool isDeceased;
        bool exists;
        address[] contactList;
        uint256[] vaultList; // Changed from address[] to uint256[]
    }

    struct Contact {
        address addr;
        string firstName;
        string lastName;
        string email;
        string phone;
        bool hasVotingRight;
        bool isVerified;
        bool exists;
        uint256[] authorizedVaults; // Changed from address[] to uint256[]
    }

    struct Vault {
        uint256 id; // unique vault ID
        string name;
        address owner;
        bool isReleased;
        string cypherIv;
        string encryptionKey;
        bool exists;
        address[] authorizedContacts;
        uint256[] fileIds; // Changed from string[] to uint256[]
    }

    struct VaultFile {
        uint256 id; // unique file ID
        string originalName;
        string mimeType;
        string cid;
        string uploadDate;
        bool exists;
    }

    struct ContactVaultDetails {
        uint256 vaultId;
        string vaultName;
        address vaultOwner;
        bool isReleased;
        string cypherIv;
        string encryptionKey;
        uint256[] fileIds;
    }

    struct DeathDeclaration {
        address initiator;
        uint256 startTime;
        uint256 votesFor;
        uint256 votesAgainst;
        uint256 totalVotingContacts;
        bool isActive;
        bool consensusReached;
        uint256 gracePeriodEnd; // Timestamp when grace period ends
        bool isInGracePeriod; // True when consensus reached but grace period active
        mapping(address => bool) hasVoted;
        mapping(address => bool) vote; // true = for death, false = against
    }

    // State variables
    mapping(address => Owner) private owners;
    mapping(address => mapping(address => Contact)) private contacts; // owner => contact => Contact
    mapping(uint256 => Vault) private vaults; // vault ID => Vault
    mapping(uint256 => mapping(uint256 => VaultFile)) private vaultFiles; // vault ID => file ID => VaultFile
    mapping(address => DeathDeclaration) private deathDeclarations;
    mapping(address => ContactVaultDetails[]) private contactVaultDetails; // contact address => array of vault details
    uint256 private vaultCounter = 0; // Counter for generating unique vault IDs
    uint256 private fileCounter = 0; // Counter for generating unique file IDs

    // Events
    event OwnerRegistered(address indexed owner, string firstName, string lastName);
    event ContactAdded(address indexed owner, address indexed contact, string firstName, string lastName, bool hasVotingRight);
    event ContactVerified(address indexed owner, address indexed contact);
    event HeartbeatSent(address indexed owner, uint256 timestamp);
    event DeathDeclared(address indexed owner, address indexed declaredBy, uint256 timestamp);
    event VoteCast(address indexed owner, address indexed voter, bool vote);
    event ConsensusReached(address indexed owner, bool isDeceased, uint256 timestamp);
    event GracePeriodStarted(address indexed owner, uint256 gracePeriodEnd);
    event VaultCreated(uint256 indexed vaultId, address indexed owner, string name);
    event VaultFileAdded(uint256 indexed vaultId, uint256 fileId, string originalName);
    event VaultReleased(uint256 indexed vaultId, address indexed owner);
    event VaultContactAuthorized(uint256 indexed vaultId, address indexed contact);

    // Modifiers
    modifier onlyRegistered() {
        require(owners[msg.sender].exists, "Owner not registered");
        _;
    }

    modifier onlyContact(address owner) {
        require(contacts[owner][msg.sender].exists, "Not a contact of this owner");
        _;
    }

    modifier onlyVerifiedContact(address owner) {
        require(contacts[owner][msg.sender].exists, "Not a contact of this owner");
        require(contacts[owner][msg.sender].isVerified, "Contact not verified");
        _;
    }

    modifier onlyVotingContact(address owner) {
        require(contacts[owner][msg.sender].exists, "Not a contact of this owner");
        // Removed verification requirement for POC
        // require(contacts[owner][msg.sender].isVerified, "Contact not verified");
        require(contacts[owner][msg.sender].hasVotingRight, "No voting rights");
        _;
    }

    modifier onlyVaultOwner(uint256 vaultId) {
        require(vaults[vaultId].exists, "Vault does not exist");
        require(vaults[vaultId].owner == msg.sender, "Not vault owner");
        _;
    }

    modifier onlyVaultAuthorized(uint256 vaultId) {
        require(vaults[vaultId].exists, "Vault does not exist");
        require(vaults[vaultId].owner == msg.sender || _isAuthorizedContact(vaultId, msg.sender), "Not authorized");
        _;
    }

    // Internal helper function
    function _isAuthorizedContact(uint256 vaultId, address contact) internal view returns (bool) {
        Vault storage vault = vaults[vaultId];
        for (uint i = 0; i < vault.authorizedContacts.length; i++) {
            if (vault.authorizedContacts[i] == contact) {
                return true;
            }
        }
        return false;
    }

    /**
     * @dev Register as an owner
     */
    function registerOwner(
        string calldata _firstName,
        string calldata _lastName,
        string calldata _email,
        string calldata _phone,
        uint256 _graceInterval
    ) external {
        require(!owners[msg.sender].exists, "Owner already registered");
        require(_graceInterval >= 1 days, "Grace interval too short");
        require(_graceInterval <= 365 days, "Grace interval too long");

        owners[msg.sender] = Owner({
            addr: msg.sender,
            firstName: _firstName,
            lastName: _lastName,
            email: _email,
            phone: _phone,
            lastHeartbeat: block.timestamp,
            graceInterval: _graceInterval,
            isDeceased: false,
            exists: true,
            contactList: new address[](0),
            vaultList: new uint256[](0)
        });

        emit OwnerRegistered(msg.sender, _firstName, _lastName);
    }

    /**
     * @dev Add a contact to the owner's list
     */
    function addContact(
        address _contact,
        string calldata _firstName,
        string calldata _lastName,
        string calldata _email,
        string calldata _phone,
        bool _hasVotingRight,
        uint256[] calldata _authorizedVaults
    ) external onlyRegistered {
        require(_contact != msg.sender, "Cannot add yourself as contact");
        require(!contacts[msg.sender][_contact].exists, "Contact already exists");

        contacts[msg.sender][_contact] = Contact({
            addr: _contact,
            firstName: _firstName,
            lastName: _lastName,
            email: _email,
            phone: _phone,
            hasVotingRight: _hasVotingRight,
            isVerified: true, // Auto-verified for POC
            exists: true,
            authorizedVaults: new uint256[](0)
        });

        owners[msg.sender].contactList.push(_contact);

        // Authorize contact for specified vaults
        for (uint i = 0; i < _authorizedVaults.length; i++) {
            uint256 vaultId = _authorizedVaults[i];
            require(vaults[vaultId].exists, "Vault does not exist");
            require(vaults[vaultId].owner == msg.sender, "Not vault owner");
            require(!_isAuthorizedContact(vaultId, _contact), "Contact already authorized for this vault");

            vaults[vaultId].authorizedContacts.push(_contact);
            contacts[msg.sender][_contact].authorizedVaults.push(vaultId);

            // Add vault details to contact's vault details mapping
            Vault storage vault = vaults[vaultId];
            ContactVaultDetails memory newDetail = ContactVaultDetails({
                vaultId: vaultId,
                vaultName: vault.name,
                vaultOwner: vault.owner,
                isReleased: vault.isReleased,
                cypherIv: vault.cypherIv,
                encryptionKey: vault.encryptionKey,
                fileIds: vault.fileIds
            });
            contactVaultDetails[_contact].push(newDetail);

            emit VaultContactAuthorized(vaultId, _contact);
        }

        emit ContactAdded(msg.sender, _contact, _firstName, _lastName, _hasVotingRight);
    }

    /**
     * @dev Verify a contact (called by the contact themselves)
     */
    function verifyContact(address _owner) external onlyContact(_owner) {
        require(!contacts[_owner][msg.sender].isVerified, "Already verified");
        contacts[_owner][msg.sender].isVerified = true;
        emit ContactVerified(_owner, msg.sender);
    }

    /**
     * @dev Update contact information (called by the contact themselves)
     */
    function updateContactInfo(
        address _owner,
        string calldata _firstName,
        string calldata _lastName,
        string calldata _email,
        string calldata _phone
    ) external onlyContact(_owner) {
        Contact storage contact = contacts[_owner][msg.sender];
        contact.firstName = _firstName;
        contact.lastName = _lastName;
        contact.email = _email;
        contact.phone = _phone;
    }

    /**
     * @dev Send heartbeat to prove owner is alive
     */
    function sendHeartbeat() external onlyRegistered {
        require(!owners[msg.sender].isDeceased, "Owner is already deceased");

        owners[msg.sender].lastHeartbeat = block.timestamp;

        DeathDeclaration storage declaration = deathDeclarations[msg.sender];

        // Cancel any active death declaration or grace period
        if (declaration.isActive || declaration.isInGracePeriod) {
            declaration.isActive = false;
            declaration.isInGracePeriod = false;
            declaration.consensusReached = false;
            declaration.gracePeriodEnd = 0;
            emit ConsensusReached(msg.sender, false, block.timestamp);
        }

        emit HeartbeatSent(msg.sender, block.timestamp);
    }

    /**
     * @dev Initiate death declaration voting
     */
    function declareDeceased(address _owner) external onlyVotingContact(_owner) {
        require(owners[_owner].exists, "Owner not registered");
        require(!owners[_owner].isDeceased, "Owner already deceased");
        require(!deathDeclarations[_owner].isActive, "Death declaration already active");

        // Count total voting contacts
        uint256 votingContacts = 0;
        for (uint i = 0; i < owners[_owner].contactList.length; i++) {
            address contactAddr = owners[_owner].contactList[i];
            // Removed verification requirement for POC - only check voting rights
            if (contacts[_owner][contactAddr].hasVotingRight) {
                votingContacts++;
            }
        }

        require(votingContacts > 0, "No voting contacts available");

        // Initialize death declaration
        DeathDeclaration storage declaration = deathDeclarations[_owner];
        declaration.initiator = msg.sender;
        declaration.startTime = block.timestamp;
        declaration.votesFor = 1; // Initiator votes for death
        declaration.votesAgainst = 0;
        declaration.totalVotingContacts = votingContacts;
        declaration.isActive = true;
        declaration.consensusReached = false;
        declaration.isInGracePeriod = false;
        declaration.gracePeriodEnd = 0;
        declaration.hasVoted[msg.sender] = true;
        declaration.vote[msg.sender] = true;

        emit DeathDeclared(_owner, msg.sender, block.timestamp);
        emit VoteCast(_owner, msg.sender, true);

        // Check if consensus is reached immediately
        _checkConsensus(_owner);
    }

    /**
     * @dev Vote on death declaration
     */
    function voteOnDeathDeclaration(address _owner, bool _vote) external onlyVotingContact(_owner) {
        require(deathDeclarations[_owner].isActive, "No active death declaration");
        require(!deathDeclarations[_owner].hasVoted[msg.sender], "Already voted");

        DeathDeclaration storage declaration = deathDeclarations[_owner];
        declaration.hasVoted[msg.sender] = true;
        declaration.vote[msg.sender] = _vote;

        if (_vote) {
            declaration.votesFor++;
        } else {
            declaration.votesAgainst++;
        }

        emit VoteCast(_owner, msg.sender, _vote);
        _checkConsensus(_owner);
    }

    /**
     * @dev Finalize death declaration after grace period expires
     */
    function finalizeDeathDeclaration(address _owner) external {
        DeathDeclaration storage declaration = deathDeclarations[_owner];
        require(declaration.isInGracePeriod, "Not in grace period");
        require(block.timestamp >= declaration.gracePeriodEnd, "Grace period not yet expired");
        require(!owners[_owner].isDeceased, "Owner already deceased");

        // Finalize death
        owners[_owner].isDeceased = true;
        declaration.isActive = false;
        declaration.isInGracePeriod = false;

        emit ConsensusReached(_owner, true, block.timestamp);
    }

    /**
     * @dev Check if grace period has expired and auto-finalize if needed
     */
    function checkGracePeriodExpiry(address _owner) external view returns (bool expired, bool canFinalize) {
        DeathDeclaration storage declaration = deathDeclarations[_owner];
        expired = declaration.isInGracePeriod && block.timestamp >= declaration.gracePeriodEnd;
        canFinalize = expired && !owners[_owner].isDeceased;
        return (expired, canFinalize);
    }

    /**
     * @dev Internal function to check if consensus is reached
     */
    function _checkConsensus(address _owner) internal {
        DeathDeclaration storage declaration = deathDeclarations[_owner];
        uint256 requiredVotes = (declaration.totalVotingContacts * 50) / 100 + 1; // Majority

        if (declaration.votesFor >= requiredVotes) {
            // Death consensus reached - start 30 second grace period
            declaration.consensusReached = true;
            declaration.isInGracePeriod = true;
            declaration.gracePeriodEnd = block.timestamp + 30; // 30 second grace period
            emit ConsensusReached(_owner, true, block.timestamp);
            emit GracePeriodStarted(_owner, declaration.gracePeriodEnd);
        } else if (declaration.votesAgainst >= requiredVotes) {
            // Death rejected
            declaration.isActive = false;
            declaration.consensusReached = true;
            declaration.isInGracePeriod = false;
            emit ConsensusReached(_owner, false, block.timestamp);
        }
        // If neither condition is met, voting continues
    }

    // View functions
    function getOwnerInfo(address _owner) external view returns (
        string memory firstName,
        string memory lastName,
        uint256 lastHeartbeat,
        uint256 graceInterval,
        bool isDeceased,
        bool exists
    ) {
        Owner storage owner = owners[_owner];
        return (
            owner.firstName,
            owner.lastName,
            owner.lastHeartbeat,
            owner.graceInterval,
            owner.isDeceased,
            owner.exists
        );
    }

    function getContactInfo(address _owner, address _contact) external view returns (
        address addr,
        string memory firstName,
        string memory lastName,
        string memory email,
        string memory phone,
        bool hasVotingRight,
        bool isVerified,
        bool exists
    ) {
        Contact storage contact = contacts[_owner][_contact];
        return (
            contact.addr,
            contact.firstName,
            contact.lastName,
            contact.email,
            contact.phone,
            contact.hasVotingRight,
            contact.isVerified,
            contact.exists
        );
    }

    function getDeathDeclarationStatus(address _owner) external view returns (
        bool isActive,
        bool isDeceased,
        uint256 votesFor,
        uint256 votesAgainst,
        uint256 totalVotingContacts,
        bool consensusReached,
        bool isInGracePeriod,
        uint256 gracePeriodEnd
    ) {
        DeathDeclaration storage declaration = deathDeclarations[_owner];
        return (
            declaration.isActive,
            owners[_owner].isDeceased,
            declaration.votesFor,
            declaration.votesAgainst,
            declaration.totalVotingContacts,
            declaration.consensusReached,
            declaration.isInGracePeriod,
            declaration.gracePeriodEnd
        );
    }

    function getContactList(address _owner) external view returns (address[] memory) {
        return owners[_owner].contactList;
    }

    /**
     * @dev Get detailed contact list with all contact information
     */
    function getContactListDetails(address _owner) external view returns (
        address[] memory contactAddresses,
        string[] memory firstNames,
        string[] memory lastNames,
        string[] memory emails,
        string[] memory phones,
        bool[] memory hasVotingRights,
        bool[] memory isVerified
    ) {
        address[] memory contactList = owners[_owner].contactList;
        uint256 length = contactList.length;

        contactAddresses = new address[](length);
        firstNames = new string[](length);
        lastNames = new string[](length);
        emails = new string[](length);
        phones = new string[](length);
        hasVotingRights = new bool[](length);
        isVerified = new bool[](length);

        for (uint256 i = 0; i < length; i++) {
            address contactAddr = contactList[i];
            Contact storage contact = contacts[_owner][contactAddr];

            contactAddresses[i] = contact.addr;
            firstNames[i] = contact.firstName;
            lastNames[i] = contact.lastName;
            emails[i] = contact.email;
            phones[i] = contact.phone;
            hasVotingRights[i] = contact.hasVotingRight;
            isVerified[i] = contact.isVerified;
        }
    }

    function hasVoted(address _owner, address _voter) external view returns (bool) {
        return deathDeclarations[_owner].hasVoted[_voter];
    }

    function getVote(address _owner, address _voter) external view returns (bool) {
        require(deathDeclarations[_owner].hasVoted[_voter], "Voter has not voted");
        return deathDeclarations[_owner].vote[_voter];
    }

    /**
     * @dev Get contact's authorized vaults
     */
    function getContactAuthorizedVaults(address _owner, address _contact) external view returns (uint256[] memory) {
        Contact storage contact = contacts[_owner][_contact];
        return contact.authorizedVaults;
    }

    // Vault Management Functions

    /**
     * @dev Create a new vault
     */
    function createVault(
        string calldata _name,
        string calldata _cypherIv,
        string calldata _encryptionKey
    ) external onlyRegistered returns (uint256) {
        uint256 vaultId = vaultCounter;
        require(!vaults[vaultId].exists, "Vault already exists");

        vaults[vaultId] = Vault({
            id: vaultId,
            name: _name,
            owner: msg.sender,
            isReleased: false,
            cypherIv: _cypherIv,
            encryptionKey: _encryptionKey,
            exists: true,
            authorizedContacts: new address[](0),
            fileIds: new uint256[](0)
        });

        owners[msg.sender].vaultList.push(vaultId);
        vaultCounter++;

        emit VaultCreated(vaultId, msg.sender, _name);
        return vaultId;
    }

    /**
     * @dev Add a file to a vault
     */
    function addVaultFile(
        uint256 _vaultId,
        string calldata _originalName,
        string calldata _mimeType,
        string calldata _cid,
        string calldata _uploadDate
    ) external onlyVaultOwner(_vaultId) returns (uint256) {
        uint256 fileId = fileCounter;
        require(!vaultFiles[_vaultId][fileId].exists, "File already exists");

        vaultFiles[_vaultId][fileId] = VaultFile({
            id: fileId,
            originalName: _originalName,
            mimeType: _mimeType,
            cid: _cid,
            uploadDate: _uploadDate,
            exists: true
        });

        vaults[_vaultId].fileIds.push(fileId);
        fileCounter++;

        emit VaultFileAdded(_vaultId, fileId, _originalName);
        return fileId;
    }

    /**
     * @dev Authorize a contact to access a vault
     */
    function authorizeVaultContact(
        uint256 _vaultId,
        address _contact
    ) external onlyVaultOwner(_vaultId) {
        require(contacts[msg.sender][_contact].exists, "Contact does not exist");
        require(!_isAuthorizedContact(_vaultId, _contact), "Contact already authorized");

        vaults[_vaultId].authorizedContacts.push(_contact);

        // Add vault to contact's authorized vaults list
        contacts[msg.sender][_contact].authorizedVaults.push(_vaultId);

        // Add vault details to contact's vault details mapping
        Vault storage vault = vaults[_vaultId];
        ContactVaultDetails memory newDetail = ContactVaultDetails({
            vaultId: _vaultId,
            vaultName: vault.name,
            vaultOwner: vault.owner,
            isReleased: vault.isReleased,
            cypherIv: vault.cypherIv,
            encryptionKey: vault.encryptionKey,
            fileIds: vault.fileIds
        });
        contactVaultDetails[_contact].push(newDetail);

        emit VaultContactAuthorized(_vaultId, _contact);
    }

    /**
     * @dev Release a vault (called when owner is deceased)
     */
    function releaseVault(uint256 _vaultId) external onlyVaultAuthorized(_vaultId) {
        require(!vaults[_vaultId].isReleased, "Vault already released");
        require(owners[vaults[_vaultId].owner].isDeceased, "Owner not deceased");

        vaults[_vaultId].isReleased = true;
        emit VaultReleased(_vaultId, vaults[_vaultId].owner);
    }

    /**
     * @dev Get vault information
     */
    function getVaultInfo(uint256 _vaultId) external view returns (
        uint256 id,
        string memory name,
        address owner,
        bool isReleased,
        string memory cypherIv,
        string memory encryptionKey,
        uint256[] memory fileIds,
        address[] memory authorizedContacts,
        bool exists
    ) {
        Vault storage vault = vaults[_vaultId];
        return (
            vault.id,
            vault.name,
            vault.owner,
            vault.isReleased,
            vault.cypherIv,
            vault.encryptionKey,
            vault.fileIds,
            vault.authorizedContacts,
            vault.exists
        );
    }

    /**
     * @dev Get vault file information
     */
    function getVaultFileInfo(uint256 _vaultId, uint256 _fileId) external view returns (
        string memory originalName,
        string memory mimeType,
        string memory cid,
        string memory uploadDate,
        bool exists
    ) {
        VaultFile storage file = vaultFiles[_vaultId][_fileId];
        return (
            file.originalName,
            file.mimeType,
            file.cid,
            file.uploadDate,
            file.exists
        );
    }

    /**
     * @dev Get vault's authorized contacts
     */
    function getVaultAuthorizedContacts(uint256 _vaultId) external view returns (address[] memory) {
        return vaults[_vaultId].authorizedContacts;
    }

    /**
     * @dev Get owner's vault list
     */
    function getOwnerVaultList(address _owner) external view returns (uint256[] memory) {
        return owners[_owner].vaultList;
    }

    /**
     * @dev Get detailed vault list with all vault information
     */
    function getOwnerVaultListDetails(address _owner) external view returns (
        uint256[] memory vaultIds,
        string[] memory names,
        address[] memory vaultOwners,
        bool[] memory isReleased,
        string[] memory cypherIvs,
        string[] memory encryptionKeys,
        uint256[][] memory fileIds,
        address[][] memory authorizedContacts
    ) {
        uint256[] memory vaultList = owners[_owner].vaultList;
        uint256 length = vaultList.length;

        vaultIds = new uint256[](length);
        names = new string[](length);
        vaultOwners = new address[](length);
        isReleased = new bool[](length);
        cypherIvs = new string[](length);
        encryptionKeys = new string[](length);
        fileIds = new uint256[][](length);
        authorizedContacts = new address[][](length);

        for (uint256 i = 0; i < length; i++) {
            uint256 vaultId = vaultList[i];
            Vault storage vault = vaults[vaultId];

            vaultIds[i] = vault.id;
            names[i] = vault.name;
            vaultOwners[i] = vault.owner;
            isReleased[i] = vault.isReleased;
            cypherIvs[i] = vault.cypherIv;
            encryptionKeys[i] = vault.encryptionKey;
            fileIds[i] = vault.fileIds;
            authorizedContacts[i] = vault.authorizedContacts;
        }
    }

    /**
     * @dev Check if address is authorized for vault
     */
    function isVaultAuthorized(uint256 _vaultId, address _address) external view returns (bool) {
        return _isAuthorizedContact(_vaultId, _address);
    }

    /**
     * @dev Get vault file list
     */
    function getVaultFileList(uint256 _vaultId) external view returns (uint256[] memory) {
        return vaults[_vaultId].fileIds;
    }

    /**
     * @dev Get contact's vault details
     */
    function getContactVaultDetails(address _contact) external view returns (
        uint256[] memory vaultIds,
        string[] memory vaultNames,
        address[] memory vaultOwners,
        bool[] memory isReleased,
        string[] memory cypherIvs,
        string[] memory encryptionKeys,
        uint256[][] memory fileIds
    ) {
        ContactVaultDetails[] storage details = contactVaultDetails[_contact];
        uint256 length = details.length;

        vaultIds = new uint256[](length);
        vaultNames = new string[](length);
        vaultOwners = new address[](length);
        isReleased = new bool[](length);
        cypherIvs = new string[](length);
        encryptionKeys = new string[](length);
        fileIds = new uint256[][](length);

        for (uint256 i = 0; i < length; i++) {
            ContactVaultDetails storage detail = details[i];
            vaultIds[i] = detail.vaultId;
            vaultNames[i] = detail.vaultName;
            vaultOwners[i] = detail.vaultOwner;
            isReleased[i] = detail.isReleased;
            cypherIvs[i] = detail.cypherIv;
            encryptionKeys[i] = detail.encryptionKey;
            fileIds[i] = detail.fileIds;
        }
    }
}

