// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title Vault
 * @dev Smart contract for managing encrypted vault data
 * @dev Corresponds to frontend Vault interface with encrypted file storage and contact management
 * @dev Data is encrypted client-side using Oasis Sapphire encryption before storage
 */
contract Vault {
    address public immutable owner;

    // Vault metadata
    string public vaultName;
    bool public isReleased;

    // Encrypted vault data (encrypted client-side with Oasis Sapphire)
    bytes private encryptedVaultData;
    bool private vaultDataSet;

    // Authorized contacts mapping: address => bool
    mapping(address => bool) private authorizedContacts;
    address[] private authorizedContactsList;

    // Events
    event VaultDataStored(address indexed by);
    event VaultDataRetrieved(address indexed by);
    event ContactAuthorized(address indexed contact, address indexed by);
    event ContactRevoked(address indexed contact, address indexed by);
    event VaultReleased(address indexed by);
    event VaultNameUpdated(string newName, address indexed by);

    // Custom errors
    error NotOwner();
    error NotAuthorized();
    error VaultDataNotSet();
    error VaultDataAlreadySet();
    error ContactAlreadyAuthorized();
    error ContactNotAuthorized();
    error VaultNotReleased();
    error EmptyVaultName();

    /**
     * @dev Constructor sets the owner and initial vault name
     * @param _vaultName The name of the vault
     */
    constructor(string memory _vaultName) {
        if (bytes(_vaultName).length == 0) {
            revert EmptyVaultName();
        }

        owner = msg.sender;
        vaultName = _vaultName;
        isReleased = false;
    }

    /**
     * @dev Modifier to restrict access to owner only
     */
    modifier onlyOwner() {
        if (msg.sender != owner) {
            revert NotOwner();
        }
        _;
    }

    /**
     * @dev Modifier to restrict access to owner or authorized contacts when vault is released
     */
    modifier onlyOwnerOrAuthorizedWhenReleased() {
        // Owner always has access
        if (msg.sender == owner) {
            _;
            return;
        }

        // If vault is not released, only owner can access
        if (!isReleased) {
            revert NotAuthorized();
        }

        // Check if contact is authorized
        if (!authorizedContacts[msg.sender]) {
            revert NotAuthorized();
        }
        _;
    }

    /**
     * @dev Store encrypted vault data - only owner can call this
     * @param _encryptedData The encrypted vault data (files, contacts, cypher info)
     */
    function setDataEncrypt(bytes calldata _encryptedData) external onlyOwner {
        if (vaultDataSet) {
            revert VaultDataAlreadySet();
        }

        encryptedVaultData = _encryptedData;
        vaultDataSet = true;

        emit VaultDataStored(msg.sender);
    }

    /**
     * @dev Retrieve encrypted vault data - only owner or authorized contacts when released
     * @return The encrypted vault data
     */
    function getVaultData() external onlyOwnerOrAuthorizedWhenReleased returns (bytes memory) {
        if (!vaultDataSet) {
            revert VaultDataNotSet();
        }

        emit VaultDataRetrieved(msg.sender);
        return encryptedVaultData;
    }

    /**
     * @dev Check if vault data has been set
     * @return True if vault data is stored, false otherwise
     */
    function isVaultDataSet() external view returns (bool) {
        return vaultDataSet;
    }

    /**
     * @dev Get all authorized contacts addresses
     * @return Array of authorized contact addresses
     */
    function getAllAuthorizedContacts() external view returns (address[] memory) {
        return authorizedContactsList;
    }

    /**
     * @dev Add a new authorized contact - only owner can call this
     * @param _contact The address of the new authorized contact
     */
    function addAuthorizedContact(address _contact) external onlyOwner {
        if (_contact == address(0)) {
            revert ContactNotAuthorized();
        }

        if (authorizedContacts[_contact]) {
            revert ContactAlreadyAuthorized();
        }

        authorizedContacts[_contact] = true;
        authorizedContactsList.push(_contact);

        emit ContactAuthorized(_contact, msg.sender);
    }

    /**
     * @dev Remove an authorized contact - only owner can call this
     * @param _contact The address of the contact to remove from authorized contacts
     */
    function removeAuthorizedContact(address _contact) external onlyOwner {
        if (!authorizedContacts[_contact]) {
            revert ContactNotAuthorized();
        }

        authorizedContacts[_contact] = false;

        // Remove from list
        for (uint i = 0; i < authorizedContactsList.length; i++) {
            if (authorizedContactsList[i] == _contact) {
                // Replace with last element and pop
                authorizedContactsList[i] = authorizedContactsList[authorizedContactsList.length - 1];
                authorizedContactsList.pop();
                break;
            }
        }

        emit ContactRevoked(_contact, msg.sender);
    }

    /**
     * @dev Check if a contact is authorized
     * @param _contact The address of the contact to check
     * @return True if the contact is authorized, false otherwise
     */
    function isContactAuthorized(address _contact) external view returns (bool) {
        return authorizedContacts[_contact];
    }

    /**
     * @dev Release the vault - only owner can call this
     * @dev This allows authorized contacts to access the vault data
     */
    function releaseVault() external onlyOwner {
        isReleased = true;
        emit VaultReleased(msg.sender);
    }

    /**
     * @dev Update vault name - only owner can call this
     * @param _newName The new name for the vault
     */
    function updateVaultName(string memory _newName) external onlyOwner {
        if (bytes(_newName).length == 0) {
            revert EmptyVaultName();
        }

        vaultName = _newName;
        emit VaultNameUpdated(_newName, msg.sender);
    }

    /**
     * @dev Get vault information
     * @return _owner The vault owner address
     * @return _name The vault name
     * @return _released Whether the vault is released
     * @return _dataSet Whether vault data is set
     * @return _contactCount Number of authorized contacts
     */
    function getVaultInfo() external view returns (
        address _owner,
        string memory _name,
        bool _released,
        bool _dataSet,
        uint256 _contactCount
    ) {
        return (
            owner,
            vaultName,
            isReleased,
            vaultDataSet,
            authorizedContactsList.length
        );
    }

    /**
     * @dev Get the owner address
     * @return The owner address
     */
    function getOwner() external view returns (address) {
        return owner;
    }

    /**
     * @dev Debug function to check authorization status
     * @return sender The message sender
     * @return contractOwner The contract owner
     * @return isOwnerMatch Whether sender is owner
     * @return isInContacts Whether sender is in authorized contacts
     * @return vaultReleased Whether vault is released
     * @return shouldPass Whether access should be granted
     */
    function debugAuth() external view returns (
        address sender,
        address contractOwner,
        bool isOwnerMatch,
        bool isInContacts,
        bool vaultReleased,
        bool shouldPass
    ) {
        sender = msg.sender;
        contractOwner = owner;
        isOwnerMatch = msg.sender == owner;
        isInContacts = authorizedContacts[msg.sender];
        vaultReleased = isReleased;
        shouldPass = isOwnerMatch || (vaultReleased && isInContacts);
    }
}

