// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.28;

import "./User.sol";

contract VaultManager {
    // Custom errors for gas optimization
    error Unauthorized();
    error InvalidAddress();
    error VaultNotFound();
    error ContactNotFound();
    error DuplicateContact();
    error VaultNotReleased();
    error UserNotDead();
    error InvalidVaultId();
    error VaultAlreadyExists();

    // Vault status enum
    enum VaultStatus {
        ACTIVE,
        RELEASED
    }

    // Vault struct matching frontend model
    struct Vault {
        string id;
        string name;
        address owner;
        address[] authorizedContacts;
        VaultStatus status;
        string encryptionKey; // Encrypted key stored in Sapphire
        string iv; // Initialization vector for encryption
        uint64 createdAt;
        uint64 lastUpdated;
        bool isReleased;
    }

    // Mapping from vault ID to vault data
    mapping(string => Vault) public vaults;

    // Mapping from user address to their vault IDs
    mapping(address => string[]) public userVaults;

    // Mapping to check if vault ID exists
    mapping(string => bool) public vaultExists;

    // Mapping to track all unique authorized contacts
    mapping(address => bool) public isAuthorizedContact;
    address[] public allAuthorizedContacts;

    // Reference to User contract
    User public userContract;

    // Events for better UX
    event VaultCreated(string indexed vaultId, address indexed owner, string name);
    event ContactAssigned(string indexed vaultId, address indexed contact);
    event ContactRemoved(string indexed vaultId, address indexed contact);
    event VaultReleased(string indexed vaultId, uint64 releasedAt);
    event EncryptionKeyUpdated(string indexed vaultId);
    event VaultMetadataUpdated(string indexed vaultId, string name);

    constructor(address _userContractAddress) {
        if (_userContractAddress == address(0)) revert InvalidAddress();
        userContract = User(_userContractAddress);
    }

    // Modifiers
    modifier onlyVaultOwner(string memory _vaultId) {
        if (!vaultExists[_vaultId]) revert VaultNotFound();
        if (vaults[_vaultId].owner != msg.sender) revert Unauthorized();
        _;
    }

    modifier onlyAuthorizedContact(string memory _vaultId) {
        if (!vaultExists[_vaultId]) revert VaultNotFound();

        bool isAuthorized = false;
        address[] memory contacts = vaults[_vaultId].authorizedContacts;
        for (uint256 i = 0; i < contacts.length; i++) {
            if (contacts[i] == msg.sender) {
                isAuthorized = true;
                break;
            }
        }

        if (!isAuthorized) revert Unauthorized();
        _;
    }

    modifier onlyVaultOwnerOrContact(string memory _vaultId) {
        if (!vaultExists[_vaultId]) revert VaultNotFound();

        bool isAuthorized = false;
        if (vaults[_vaultId].owner == msg.sender) {
            isAuthorized = true;
        } else {
            address[] memory contacts = vaults[_vaultId].authorizedContacts;
            for (uint256 i = 0; i < contacts.length; i++) {
                if (contacts[i] == msg.sender) {
                    isAuthorized = true;
                    break;
                }
            }
        }

        if (!isAuthorized) revert Unauthorized();
        _;
    }

    modifier onlyDeadUser() {
        User.UserStatus status = userContract.getStatus();
        if (status != User.UserStatus.DEAD) revert UserNotDead();
        _;
    }

    // Core functions
    function createVault(
        string calldata _vaultId,
        string calldata _name,
        address[] calldata _contacts,
        string calldata _encryptionKey,
        string calldata _iv
    ) external {
        if (bytes(_vaultId).length == 0) revert InvalidVaultId();
        if (vaultExists[_vaultId]) revert VaultAlreadyExists();
        if (bytes(_name).length == 0) revert InvalidVaultId();

        // Verify user is active
        User.UserStatus status = userContract.getStatus();
        if (status != User.UserStatus.ACTIVE) revert Unauthorized();

        // Verify all contacts are valid User contracts
        for (uint256 i = 0; i < _contacts.length; i++) {
            if (_contacts[i] == address(0)) revert InvalidAddress();
            // Note: In a real implementation, you'd verify these are actual User contracts
        }

        Vault storage newVault = vaults[_vaultId];
        newVault.id = _vaultId;
        newVault.name = _name;
        newVault.owner = msg.sender;
        newVault.authorizedContacts = _contacts;
        newVault.status = VaultStatus.ACTIVE;
        newVault.encryptionKey = _encryptionKey;
        newVault.iv = _iv;
        newVault.createdAt = uint64(block.timestamp);
        newVault.lastUpdated = uint64(block.timestamp);
        newVault.isReleased = false;

        vaultExists[_vaultId] = true;
        userVaults[msg.sender].push(_vaultId);

        // Add contacts to global authorized contacts list
        for (uint256 i = 0; i < _contacts.length; i++) {
            if (!isAuthorizedContact[_contacts[i]]) {
                isAuthorizedContact[_contacts[i]] = true;
                allAuthorizedContacts.push(_contacts[i]);
            }
        }

        emit VaultCreated(_vaultId, msg.sender, _name);
    }

    function assignContactToVault(string calldata _vaultId, address _contact) 
        external 
        onlyVaultOwner(_vaultId) 
    {
        if (_contact == address(0)) revert InvalidAddress();

        // Check if contact is already assigned
        address[] storage contacts = vaults[_vaultId].authorizedContacts;
        for (uint256 i = 0; i < contacts.length; i++) {
            if (contacts[i] == _contact) revert DuplicateContact();
        }

        contacts.push(_contact);
        vaults[_vaultId].lastUpdated = uint64(block.timestamp);

        // Add to global authorized contacts list if not already there
        if (!isAuthorizedContact[_contact]) {
            isAuthorizedContact[_contact] = true;
            allAuthorizedContacts.push(_contact);
        }

        emit ContactAssigned(_vaultId, _contact);
    }

    function removeContactFromVault(string calldata _vaultId, address _contact) 
        external 
        onlyVaultOwner(_vaultId) 
    {
        if (_contact == address(0)) revert InvalidAddress();

        address[] storage contacts = vaults[_vaultId].authorizedContacts;
        uint256 contactIndex = type(uint256).max;

        for (uint256 i = 0; i < contacts.length; i++) {
            if (contacts[i] == _contact) {
                contactIndex = i;
                break;
            }
        }

        if (contactIndex == type(uint256).max) revert ContactNotFound();

        // Gas efficient removal: swap with last element and pop
        contacts[contactIndex] = contacts[contacts.length - 1];
        contacts.pop();

        vaults[_vaultId].lastUpdated = uint64(block.timestamp);

        // Check if this contact is still used in other vaults
        bool stillUsed = false;
        for (uint256 i = 0; i < allAuthorizedContacts.length; i++) {
            if (allAuthorizedContacts[i] == _contact) {
                // Check if this contact is still in any vault
                for (uint256 j = 0; j < userVaults[_contact].length; j++) {
                    string memory vaultId = userVaults[_contact][j];
                    if (vaultExists[vaultId]) {
                        address[] storage vaultContacts = vaults[vaultId].authorizedContacts;
                        for (uint256 k = 0; k < vaultContacts.length; k++) {
                            if (vaultContacts[k] == _contact) {
                                stillUsed = true;
                                break;
                            }
                        }
                        if (stillUsed) break;
                    }
                }
                break;
            }
        }

        // Remove from global list if not used anywhere else
        if (!stillUsed) {
            isAuthorizedContact[_contact] = false;
            // Remove from allAuthorizedContacts array
            for (uint256 i = 0; i < allAuthorizedContacts.length; i++) {
                if (allAuthorizedContacts[i] == _contact) {
                    allAuthorizedContacts[i] = allAuthorizedContacts[allAuthorizedContacts.length - 1];
                    allAuthorizedContacts.pop();
                    break;
                }
            }
        }

        emit ContactRemoved(_vaultId, _contact);
    }

    function getVaultInfo(string calldata _vaultId) 
        external 
        view 
        onlyVaultOwnerOrContact(_vaultId)
        returns (
            string memory id,
            string memory name,
            address owner,
            address[] memory authorizedContacts,
            VaultStatus status,
            uint64 createdAt,
            uint64 lastUpdated,
            bool isReleased
        ) 
    {
        Vault storage vault = vaults[_vaultId];
        return (
            vault.id,
            vault.name,
            vault.owner,
            vault.authorizedContacts,
            vault.status,
            vault.createdAt,
            vault.lastUpdated,
            vault.isReleased
        );
    }

    function getDecryptionKey(string calldata _vaultId) 
        external 
        view 
        onlyAuthorizedContact(_vaultId)
        onlyDeadUser
        returns (string memory encryptionKey, string memory iv) 
    {
        Vault storage vault = vaults[_vaultId];
        if (!vault.isReleased) revert VaultNotReleased();

        return (vault.encryptionKey, vault.iv);
    }

    function releaseVault(string calldata _vaultId) 
        external 
        onlyAuthorizedContact(_vaultId)
        onlyDeadUser
    {
        Vault storage vault = vaults[_vaultId];
        if (vault.isReleased) revert VaultNotReleased();

        vault.isReleased = true;
        vault.status = VaultStatus.RELEASED;
        vault.lastUpdated = uint64(block.timestamp);

        emit VaultReleased(_vaultId, uint64(block.timestamp));
    }

    function updateVaultMetadata(string calldata _vaultId, string calldata _name) 
        external 
        onlyVaultOwner(_vaultId) 
    {
        if (bytes(_name).length == 0) revert InvalidVaultId();

        vaults[_vaultId].name = _name;
        vaults[_vaultId].lastUpdated = uint64(block.timestamp);

        emit VaultMetadataUpdated(_vaultId, _name);
    }

    function updateEncryptionKey(
        string calldata _vaultId, 
        string calldata _newEncryptionKey, 
        string calldata _newIv
    ) 
        external 
        onlyVaultOwner(_vaultId) 
    {
        vaults[_vaultId].encryptionKey = _newEncryptionKey;
        vaults[_vaultId].iv = _newIv;
        vaults[_vaultId].lastUpdated = uint64(block.timestamp);

        emit EncryptionKeyUpdated(_vaultId);
    }

    // View functions for frontend integration
    function getUserVaults(address _user) external view returns (string[] memory) {
        return userVaults[_user];
    }

    // Get all addresses that are authorized contacts in any vault
    function getAllAuthorizedContacts() external view returns (address[] memory) {
        return allAuthorizedContacts;
    }

    // Alternative: Get authorized contacts for a specific vault
    function getVaultAuthorizedContacts(string calldata _vaultId) external view returns (address[] memory) {
        if (!vaultExists[_vaultId]) revert VaultNotFound();
        return vaults[_vaultId].authorizedContacts;
    }

    function getUserVaultCount(address _user) external view returns (uint256) {
        return userVaults[_user].length;
    }

    function getVaultContactCount(string calldata _vaultId) external view returns (uint256) {
        if (!vaultExists[_vaultId]) revert VaultNotFound();
        return vaults[_vaultId].authorizedContacts.length;
    }

    function getVaultContactAtIndex(string calldata _vaultId, uint256 _index) 
        external 
        view 
        returns (address) 
    {
        if (!vaultExists[_vaultId]) revert VaultNotFound();
        address[] storage contacts = vaults[_vaultId].authorizedContacts;
        if (_index >= contacts.length) revert ContactNotFound();
        return contacts[_index];
    }

    function isVaultContact(string calldata _vaultId, address _contact) 
        external 
        view 
        returns (bool) 
    {
        if (!vaultExists[_vaultId]) revert VaultNotFound();

        address[] storage contacts = vaults[_vaultId].authorizedContacts;
        for (uint256 i = 0; i < contacts.length; i++) {
            if (contacts[i] == _contact) return true;
        }
        return false;
    }

    function isVaultOwner(string calldata _vaultId, address _user) 
        external 
        view 
        returns (bool) 
    {
        if (!vaultExists[_vaultId]) revert VaultNotFound();
        return vaults[_vaultId].owner == _user;
    }

    function isVaultReleased(string calldata _vaultId) 
        external 
        view 
        returns (bool) 
    {
        if (!vaultExists[_vaultId]) revert VaultNotFound();
        return vaults[_vaultId].isReleased;
    }

    // Batch operations for frontend efficiency
    function getMultipleVaultsInfo(string[] calldata _vaultIds) 
        external 
        view 
        returns (
            string[] memory ids,
            string[] memory names,
            address[] memory owners,
            VaultStatus[] memory statuses,
            bool[] memory isReleasedArray
        ) 
    {
        uint256 length = _vaultIds.length;
        ids = new string[](length);
        names = new string[](length);
        owners = new address[](length);
        statuses = new VaultStatus[](length);
        isReleasedArray = new bool[](length);

        for (uint256 i = 0; i < length; i++) {
            if (vaultExists[_vaultIds[i]]) {
                Vault storage vault = vaults[_vaultIds[i]];
                ids[i] = vault.id;
                names[i] = vault.name;
                owners[i] = vault.owner;
                statuses[i] = vault.status;
                isReleasedArray[i] = vault.isReleased;
            }
        }

        return (ids, names, owners, statuses, isReleasedArray);
    }

    // Emergency functions (only vault owner)
    function deleteVault(string calldata _vaultId) external onlyVaultOwner(_vaultId) {
        // Remove from user's vault list
        string[] storage userVaultList = userVaults[msg.sender];
        for (uint256 i = 0; i < userVaultList.length; i++) {
            if (keccak256(bytes(userVaultList[i])) == keccak256(bytes(_vaultId))) {
                // Gas efficient removal
                userVaultList[i] = userVaultList[userVaultList.length - 1];
                userVaultList.pop();
                break;
            }
        }

        // Clear vault data
        delete vaults[_vaultId];
        delete vaultExists[_vaultId];
    }
}

